import React from "react";
import Carousel, { Modal, ModalGateway } from "react-images";
import photo1 from "./../img/portfolio/1.jpg";
import photo2 from "./../img/portfolio/2.jpg";
import photo3 from "./../img/portfolio/3.jpg";
import photo4 from "./../img/portfolio/4.jpg";

import photo5 from "./../img/portfolio/5.jpg";
import photo6 from "./../img/portfolio/6.jpg";
import photo7 from "./../img/portfolio/8.jpg";
import photo8 from "./../img/portfolio/4.jpg";

import realizacje from "./../img/ostatnie_realizacje.svg"

const images = [
  { source: photo1 },
  { source: photo2 },
  { source: photo3 },
  { source: photo4 },
];

class Portfolio extends React.Component {
  state = { modalIsOpen: false, selectedIndex: 1 };
  getStyle = (linkPhoto) => {
    return {
      backgroundImage: `url('${linkPhoto}')`,
      backgroundSize: "cover",
      backgroundPosition: "center center",
    };
  };
  toggleModal = () => {
    this.setState((state) => ({ modalIsOpen: !state.modalIsOpen }));
  };

  handleClick = (e) => {
   const currentIndex = e.target.dataset.index;
    this.setState((state) => ({
      modalIsOpen: !state.modalIsOpen,
      selectedIndex: currentIndex,
    }));
  };

  render() {
    const { modalIsOpen } = this.state;

    return (
      <>
        <ModalGateway>
          {modalIsOpen ? (
            <Modal onClose={this.toggleModal}>
              <Carousel
                currentIndex={this.state.selectedIndex}
                views={images}
              />
            </Modal>
          ) : null}
        </ModalGateway>
        <div className="portfolio">
          <div className="portfolio__container container">
            <div className="portfolio__title"><img src={realizacje}/></div>
            <div className="portfolio__item-wrap">
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo1)}
                data-index={0}
                className="portfolio__item"
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo2)}
                className="portfolio__item"
                data-index={1}
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo3)}
                className="portfolio__item"
                data-index={2}
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo4)}
                className="portfolio__item"
                data-index={3}
              ></div>
                <div
                onClick={this.handleClick}
                style={this.getStyle(photo1)}
                data-index={0}
                className="portfolio__item"
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo2)}
                className="portfolio__item"
                data-index={1}
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo3)}
                className="portfolio__item"
                data-index={2}
              ></div>
              <div
                onClick={this.handleClick}
                style={this.getStyle(photo4)}
                className="portfolio__item"
                data-index={3}
              ></div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

export default Portfolio;

