import React, { Component } from 'react';
import Lightbox from 'react-lightbox-component';
import photo from "./../img/print.jpg";

class LightBoxTest extends Component {
  constructor(props) {
    super(props);
    this.state = {
      images: [
          {
            src: photo       
          },
          {
            src: photo
          },
          {
            src: photo        
          },
          {
            src: photo       
          },
          {
            src: '../images/decorative_pic.jpg'        
          },
          {
          src: '../images/decorative_pic.jpg'  
          }
       ]
    }
  }

  render() {
    const { images } = this.state;
    return (
      <div>
        <Lightbox
          images={images}
          renderImageFunc={(idx, image, toggleLightbox, width, height) => {
            let onClick = () => null;
            const patt = /decorative/g;
            if(!patt.test(image.src)) onClick = toggleLightbox.bind(null, idx);
            return (
              <img
                key={idx}
                src={image.src}
                style={{width: width, height: height}}
                onClick={onClick} />
            )
          }}/>
      </div>
    )
  }
}

export default LightBoxTest;
